const defaultState = {
    customers: "alice"
  }

export const customersReducer = (state = defaultState, action) => {
    switch (action.type) {
      case "ADD_CASH":
        return {...state, cash: state.cash + action.payload }

      case "GET_CASH":
        return {...state, cash: state.cash - action.payload }

      default: 
        return state
    }
  }
