const defaultState = {
    list: "serduck"
  }

export const listReducer = (state = defaultState, action) => {
    switch (action.type) {
      case "ADD_ITEM":
        return {...state, cash: state.cash + action.payload }

      case "REMOVE_ITEM":
        return {...state, cash: state.cash - action.payload }

      default: 
        return state
    }
  }
